				{ answerText: 'tá', isCorrect: true },
				{ answerText: 'níl', isCorrect: false},
				{ answerText: 'Cé túsa?', isCorrect: false},

				],
		},
		{
			questionText: 'Chuala tú...',
			answerOptions: [

				{ answerText: 'cloisim an fharraige', isCorrect: true },
				{ answerText: 'cloisim an ghaoth',isCorrect: true },

			],

		},
		{
			questionText: 'Smaoinigh síar.',
			answerOptions: [
				{ answerText: 'Bhí mé in Uladh', isCorrect: false },
				{ answerText: 'Bhí mé i Laighean', isCorrect: true },
				{ answerText: 'Bhí mé sa Mhumhain', isCorrect: false },
				{ answerText: 'I gCúige Ċonnacht' },

		]	},
		{
			questionText: 'Ní shroisfaidh tú anocht í. Tabhair isteach in ár dtéanta faoinn bothán beag seo \'gainne.',
			answerOptions: [

				],
		},
		{
			questionText: '',
			answerOptions: [
				{ answerText: '', isCorrect: false },
				{ answerText: '...?', isCorrect: false },
				{ answerText: ' braon sileán', isCorrect: true },
				{ answerText: 'an fharraige', isCorrect: false },
			],
		},
		{
			questionText: 'Lasta!',
			answerOptions: [
				{ answerText: 'Níl', isCorrect: false },
				{ answerText: 'Tá', isCorrect: true },

			],
		},
		{
			questionText: 'Cé ṫú féin?',
			answerOptions: [
				{ answerText: 'Is damh seacht mbeann mé', isCorrect: true, qid:'tabhair' },
				{ answerText: 'Is seabhac mé ar aill', isCorrect: true, qid:'falsehood' },
				{ answerText: 'Is mé deor na gréine', isCorrect: true, qid:'listen' },
				{ answerText: 's damh seacht mbeann mé', isCorrect: true, qid:'leave' }
			],
		},

		{
			questionText: 'Súas',
			answerOptions: [
				{ answerText: 'Clé', isCorrect: false },
				{ answerText: 'Deas', isCorrect: false },
				{ answerText: 'Soir', isCorrect: false },
				{ answerText: 'Síos', isCorrect: true },
			],
		},
		{
			questionText: 'Súas',
			answerOptions: [
				{ answerText: 'Clé', isCorrect: false },
				{ answerText: 'Deas', isCorrect: false },
				{ answerText: 'Soir', isCorrect: false },
				{ answerText: 'Síos', isCorrect: true },
			],
		},
		{
			questionText: 'Súas',
			answerOptions: [
				{ answerText: 'Clé', isCorrect: false },
				{ answerText: 'Deas', isCorrect: false },
				{ answerText: 'Soir', isCorrect: false },
				{ answerText: 'Síos', isCorrect: true },
			],
		},
		{
			questionText: 'Súas',
			answerOptions: [
				{ answerText: 'Clé', isCorrect: false },
				{ answerText: 'Deas', isCorrect: false },
				{ answerText: 'Soir', isCorrect: false },
				{ answerText: 'Síos', isCorrect: true },
			],
		},
		{
			questionText: 'Súas',
			answerOptions: [
				{ answerText: 'Clé', isCorrect: false },
				{ answerText: 'Deas', isCorrect: false },
				{ answerText: 'Soir', isCorrect: false },
				{ answerText: 'Síos', isCorrect: true },
			],
		},
	];

const[score, setScore] = useState(0)


	const handleAnswerButtonClick = (isCorrect) => { 



		if (isCorrect) { 
setScore(score+1)		}

		const nextQuestion = currentQuestion + 1;
		setCurrentQuestion(nextQuestion);
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else { setShowScore(true)}
	}


//setup init appearance:



	return (
		<div className='app' >

			<ReactAudioPlayer src={currentQuestion === 1 ? drone : null}  autoPlay />
			<img id="app-bg" src={blackripple} className="question-img" alt="gray 3d cubes transition animation." />		

			<img id="question-img" src={stars} className={currentQuestion === 0 ? "question-img" : "hidden"} alt="wheeling starfield" />		

			<img id="question-img" src={geaga} className = {currentQuestion >= 1?  "question-img":"hidden"  } alt="must have alt" />		

			<img id="question-img" src={bg2} className = {currentQuestion >= 5 ?  
			"question-img":"hidden"  } alt="must have alt" />
			<img id="question-img" src={bg3} className={currentQuestion >= 8 ? "question-img" : "hidden"} alt="must have alt" />





			{/* HINT: replace "false" with logic to display the
      score when the user has answered all the questions */}
			{showScore ? (
				<div className='score-section'>Tá muid ar comhintinn { score } as {questions.length}</div>
			) : (
				<>
						<div className='question-section'>

						<div className='question-count'> 		
						</div>
							<div className='question-text'>{ questions[currentQuestion].questionText}</div>

						</div>

					<div className='answer-section'>
							{questions[currentQuestion].answerOptions.map((answerOption, index) => (<button key={index}
								onClick={() => handleAnswerButtonClick(answerOption.isCorrect)}

							>{answerOption.answerText}</button>))}
					</div>
				</>
			)}
		</div>
	);
}
