import {
	CircularInput,
	CircularTrack,
	CircularProgress,
	CircularThumb
} from 'react-circular-input'
import sky from './images/bgAnim5.gif';
import boxes from './images/boxes.gif';
import bg3 from './images/blinding-light-county.jpg';
